export default class SearchPanel extends Component {

    state = {
        term: ''
    };

    onSearchChange = (e) => {
        const term = e.target.value;
        
    };


    render() {
        return (
            <input 
                className = "form-control"
                style = {searchStyle}
                placeholder = {searchText}  
                value = {this.state.term} 
                onChange = {this.onSearchChange}
            />
        )
    }
}
